INSERT INTO `movie_basic` VALUES ('2023-03-24 11:54:18.396628', '2023-03-24 11:54:18.396628', '', '', '', 1, 742, 0, 0, '超豪华！！最初也是最后的大同窗会！8时J', NULL, NULL, '超豪華！！ 最初で最後の大同窓会！8時だJ', '杰尼斯超豪华！！最初也是最后的大同窗会', '日韩综艺', NULL, '90', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, '9', '日语', '杰尼斯,综艺,日本,嵐,日本综艺,日综,2018,山下智久', '超豪华！！ &lt;br /&gt; 　　最初的最后的大同窗会！8时J回来啦！！！ &lt;br /&gt; 　　泷泽秀明 岚 关八 生田斗真 山下智久 风间俊介 长谷川纯 &lt;br /&gt; 　　十代少年的超珍贵映象持续登场，陪伴多年的伙伴情谊，二十年后终于说出口的对不起和抱歉，长成立派大人的慎重感谢，和朵兄朵弟绝对不变的孩子气。 ', NULL, '/external/movie/Ftiw5-K9_fM9mxKQgp3CbS3waBUN@public', NULL, 'variety');
INSERT INTO `movie_basic` VALUES ('2023-03-24 11:54:18.447712', '2023-03-24 11:54:18.447712', '', '', '', 1, 743, 0, 0, '双层公寓：夏威夷', NULL, NULL, 'TERRACE HOUSE ALOHA STATE', '双层公寓,二层公寓：夏威夷,テラスハウスALOHASTATE', '日韩综艺', NULL, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, '9', '英语,日语', '真人秀,日本,Netflix,综艺,日综,2016,日本综艺,日剧', '湘南、東京と、舞台を移してきたテラスハウス。 &lt;br /&gt; 　　つぎなる舞台は…ハワイ！ &lt;br /&gt; 　　この美しい島に集う、 &lt;br /&gt; 　　見ず知らずの男女６人の日々は…？ &lt;br /&gt; 　　用意したのは、青い空と青い海。 &lt;br /&gt; 　　台本のない青春が再び、はじまります。 ', NULL, '/external/movie/FnwEj6mQRFcmYARt-GLbzi15PgA5@public', NULL, 'variety');
INSERT INTO `movie_basic` VALUES ('2023-03-24 11:54:18.548988', '2023-03-24 11:54:18.548988', '', '', '', 1, 744, 0, 0, '全能住宅改造王 第二季', NULL, NULL, '大改造!!劇的ビフォーアフター SEASON II', '', '日韩综艺', NULL, '', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, '9', '日语', '日本,设计,建筑,装修,全能住宅改造王,家装,综艺,纪录片', '《全能住宅改造王 》是日本一档住宅改造综艺节目，由电视台从广大报名装修遴选出改造家庭，委托有才华的设计师（多为一级建筑师）用经济的方式将窘迫破败的旧屋改造成舒适宜居的生活环境。强大的收纳功能、合理的动线设置、合理的区间规划、流动的和风日照、温馨的人文关怀，总有一点打动您。 ', NULL, '/external/movie/Fhq72K6PDf024P_meXY-p6Q8kZr7@public', NULL, 'variety');
INSERT INTO `movie_basic` VALUES ('2023-03-24 11:54:18.592272', '2023-03-24 11:54:18.592272', '', '', '', 1, 745, 0, 0, '第70届NHK红白歌会', NULL, NULL, '第70回NHK紅白歌合戦', '', '日韩综艺', NULL, '260', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 0, '9', '日语', '日本,综艺,2019,NHK,红白歌会,紅白歌合戦,日本综艺,红白', '日本频道综合日本时尚娱乐网站Modelpress报道，“第70届NHK红白歌会”出场歌手的记者发布会14日在东京涩谷的NHK电视台举行。红白两组的出场歌手共有41组，首次出场的歌手在红组有3组，白组有5组。 &lt;br /&gt; 　　红组中首次出场的歌手分别是日向坂46、米津玄师作为制作人的“Foorin”组合、LiSA，白组中首次出场的歌手分别是GENERATIONS from EXILE TRIBE、Kis-My-Ft2、菅田将晖、Official髭男dism乐队、King Gnu。 &lt;br /&gt; 　　另外，今年这届红白歌会的总主持人由搞笑组合“小內小南”成员内村光良担任，这是他继前年和去年以来，连续三年担任红白歌会的总主持人。还有一位女总主持人是久田麻由子。 &lt;br /&gt; 　　而红组主持人为女演员绫濑遥。本次是她继2013年(第64届)、2015年(第66届)之后，时隔四年第三次担任红组主持人。白组主持人由男子偶像团体“岚”成员樱井翔担任。本次是自2010年以来“岚”成员连续第五次担任白组主持人，而对于樱井翔个人而言，是继去年以来连续第二次担任主持人。 ', NULL, '/external/movie/FjMSMoUxe46caBYarEjtxlAkyYi5@public', NULL, 'variety');
